REPLACE INTO `products` (`Id`, `Code`, `Name`, `Description`, `Category_Id`, `Sub_Category_Id`, `Sub_Category2`, `Shelf_Id`, `Price`, `Currency`, `Dimension_Id`, `Dimension_Value`, `Unit_Id`, `Unit_Value`, `Stock_Quantity`, `Stock_Unit`, `CreatedAt`, `UpdatedAt`, `ImagePath`, `Brand_Id`, `IsActive`, `Almacen_Id`) VALUES
	(1, '1', 'Agarradera metálica para tubo 3/4″', 'Abrazadera metálica fabricada en acero galvanizado de alta resistencia, diseñada para sujetar tuberías de ¾″ (20 mm) a paredes, vigas o estructuras de soporte. Incluye orificios pre-taladrados para tornillos, acabado anticorrosión y perfil interior liso para evitar dañar la tubería. Ideal para instalaciones de fontanería, sistemas de riego y conducciones eléctricas empotradas.', 1, 1, 1, 1, 2.50, 'MXN', 6, '3/4', 6, 1, 136, 1, '2025-04-02 12:52:21', '2025-05-30 13:05:24', 'default.jpg', 1, 0, 1),
	(2, '2', 'Cople de Plata 1 1/2"', 'Unión roscada diseñada para empalmar tramos de tubo conduit de 1/2″ (25 mm). Fabricada en acero galvanizado de alta resistencia con acabado plateado anticorrosión, incorpora roscas NPT de 1 1/2″ para garantizar un acople estanco y mecánicamente sólido. Ideal para mantener la continuidad de la canalización eléctrica en instalaciones interiores y exteriores; protege los conductores de golpes, polvo y humedad, y facilita las labores de montaje y mantenimiento.', 2, 4, 3, 1, 49.00, 'MXN', 6, '1 1/2"', 6, 1, 12, 1, '2025-04-02 12:58:07', '2025-05-30 13:05:28', 'default.jpg', 1, 0, 1),
	(9, '9', 'Conector zapa Conduit plata 1/2″', 'Pieza de unión roscada diseñada para fijar tuberías conduit de ½″ (12,7 mm) en el “zapo” (abertura) de cajas metálicas. Fabricada en acero galvanizado con acabado plateado anticorrosión, incorpora rosca NPT ½″ según estándar ANSI B1.20.1, tuerca de fijación y arandela dentada para garantizar un montaje firme y estanco. Su perfil asegura una sujeción mecánica sólida, protege los conductores eléctricos de polvo, humedad y tensión mecánica, y facilita las labores de instalación y mantenimiento en cuadros y tableros eléctricos.', 2, 4, 2, 26, 14.00, 'MXN', 6, '1/2"', 6, 1, 53, 1, '2025-05-07 12:22:39', '2025-05-07 12:48:21', 'img\\products\\1746642159396-WhatsApp Image 2025-05-07 at 12.13.52 PM.jpeg', 1, 0, 1),
	(10, '10', 'Cople conduit plata 3/4″', 'Unión (cople) diseñada para empalmar tramos de tubo conduit de ½″ (12,7 mm). Fabricada en acero galvanizado de alta resistencia con acabado plateado anticorrosión, Ideal para mantener la continuidad de la canalización eléctrica en instalaciones residenciales, comerciales e industriales; protege los conductores frente a golpes, polvo y humedad, y facilita las tareas de montaje y mantenimiento.', 2, 4, 3, 26, 16.00, 'MXN', 6, '3/4"', 6, 1, 132, 1, '2025-05-07 12:45:42', '2025-05-07 12:46:33', 'img\\products\\1746643542588-WhatsApp Image 2025-05-07 at 12.45.06 PM.jpeg', 1, 0, 1),
	(11, '11', 'Conduit LL 1/2"  plata', 'Tubería metálica rígida de tipo LL de ½″ (12,7 mm) de diámetro interior, fabricada en acero galvanizado con costura longitudinal reforzada. Diseñada para canalizar y proteger conductores eléctricos en instalaciones residenciales, comerciales e industriales, ofrece elevada resistencia mecánica frente a impactos y vibraciones, así como protección contra polvo y humedad. ', 2, 3, 1, 1, 41.00, 'MXN', 6, '1/2"', 1, 0, 12, 1, '2025-05-07 12:52:18', '2025-06-02 10:46:29', '" "', 1, 0, 1),
	(12, '12', 'Conduit LL 3/4" plata', 'Tubería metálica rígida de tipo LL (longitudinal lockseam) de ¾″ (19,05 mm) de diámetro interior, fabricada en acero galvanizado con acabado plateado anticorrosión. Su costura longitudinal reforzada aporta alta resistencia mecánica frente a impactos, vibraciones y agentes externos (polvo, humedad). Está diseñada para canalizar y proteger conductores eléctricos en instalaciones residenciales, comerciales e industriales, y es compatible con todos los accesorios normalizados (codos, coples, conectores y cajas de derivación). Cumple con los estándares IEC 61386 y NEC para sistemas de canalización de tuberías eléctricas.', 2, 3, 1, 1, 0.00, 'MXN', 1, '3/4"', 1, 0, 6, 1, '2025-05-07 12:56:00', '2025-06-02 10:47:33', '" "', 1, 0, 1),
	(13, '13', 'Conduit T 1/2" plata', 'Tubería metálica rígida de tipo T, de ½″ (12,7 mm) de diámetro interior, fabricada en acero galvanizado con acabado plateado anticorrosión. Su costura helicoidal (spiral lock) proporciona alta resistencia mecánica a la compresión y mantiene la circularidad del tubo, facilitando el doblado con herramienta manual para trayectos con curvas suaves.', 2, 3, 1, 1, 0.00, 'MXN', 1, '1/2"', 1, 0, 13, 1, '2025-05-07 12:57:06', '2025-06-02 10:47:49', '" "', 1, 0, 1),
	(17, '17', 'Insecticida en Aerosol Raid Max de 400 ml', 'Insecticida en aerosol formulado para eliminar cucarachas e insectos rastreros. Su fórmula de acción prolongada mata al contacto y sigue actuando por hasta 3 semanas.', 9, 10, 11, 1, 103.00, 'MXN', 1, '0', 1, 0, 1, 1, '2025-05-12 14:11:25', '2025-05-12 14:11:25', 'img\\products\\1747080685423-17470806718505356621507404390738.jpg', 1, 0, 1),
	(18, '18', 'Aceite multiusos en aerosol Aflojatodo Truper 400 ml WT-400', 'Lubricante penetrante multiusos en aerosol que lubrica, protege contra la corrosión y elimina el óxido. Ideal para liberar mecanismos trabados, eliminar rechinidos y desplazar la humedad. De uso práctico en aplicaciones domésticas, automotrices e industriales.', 12, 15, 16, 1, 75.00, 'MXN', 1, '400', 1, 0, 3, 1, '2025-05-12 14:41:31', '2025-05-12 14:41:31', 'img\\products\\1747082491609-17470824562728268161662918353808.jpg', 1, 0, 1),
	(19, '19', 'Cartucho de gas butano/propano con válvula roscada Truper 275 g', 'Cartucho de gas mezcla de butano y propano con válvula roscada para conexión segura. Ideal para uso con sopletes, estufillas y lámparas portátiles. Ofrece una combustión eficiente y segura en trabajos de plomería, cocina o actividades al aire libre.', 12, 17, 18, 1, 58.00, 'MXN', 1, '0', 3, 275, 1, 1, '2025-05-12 14:50:47', '2025-05-12 14:50:47', 'img\\products\\1747083047740-17470830314606021250151129553440.jpg', 1, 0, 1),
	(20, '20', 'Cartucho de gas L.P. desechable boquilla tipo europea IUSA 275 g', 'Cartucho desechable de gas licuado de petróleo (L.P.) con boquilla tipo europea, ideal para sopletes, estufillas o equipos portátiles compatibles. Contiene mezcla de butano y proporciona una presión máxima de trabajo de 3.5 kg/cm². Práctico y seguro para trabajos técnicos o actividades al aire libre.', 12, 17, 18, 1, 58.00, 'MXN', 1, '0', 3, 275, 1, 1, '2025-05-12 14:57:51', '2025-05-12 14:57:51', 'img\\products\\1747083471214-17470834483541720074723890857977.jpg', 1, 0, 1),
